    for (let i = 0; i < fullStars; i++) {
      stars.push('‚òÖ');
    }
    if (hasHalfStar) {
      stars.push('‚òÜ');
    }
    for (let i = stars.length; i < 5; i++) {
      stars.push('‚òÜ');
    }

    return stars.join('');
  };

